HashMap<String, Integer> map = new HashMap<>();
public String all(String s, int i, int j,int index, HashMap<String, String> dp_map){
    if(i >= s.length() || j < 0) return "";   // base condition
    if(dp_map.containsKey(i +"#" + j)){
        return dp_map.get(i+"#"+j);
    }
    String start = all(s, i+1, j, index, dp_map); // getting all Prefix +  "#" + suffix  from the start
    String end = all(s, i, j-1, index, dp_map);  //  getting all Prefix +  "#" + suffix  from the end  
    map.put(start, index);
    map.put(end, index);
    dp_map.put(i+"#"+j, s.substring(0,i+1) + "#" +s.substring(j, s.length()));
    return s.substring(0,i+1) + "#" + s.substring(j, s.length());
}
public WordFilter(String[] words) {
    for(int j = 0; j < words.length; j++){
        HashMap<String, String> dp_map = new HashMap<>();
        if(j < words.length - 1 && words[j].equals(words[j+1])) continue; // if strings are same we want maximum index
        all(words[j], -1, words[j].length(), j, dp_map);
    }
}

public int f(String prefix, String suffix) {
    return map.getOrDefault(prefix + "#" + suffix, -1);
}

