class WordFilter {
public:
    map <pair<char,char>,set< pair<int,string>,greater<pair<int,string>> > > mp;
    map <pair<string,string>, int> ms;
    WordFilter(vector<string>& words) {
        for(int i=0;i<words.size();i++){
            mp[{words[i][0],words[i][words[i].size()-1]}].insert({i,words[i]});
        }
    }

    int f(string p, string s) {
        if(ms.find({p,s}) != ms.end()) return ms[{p,s}];
        int ans = -1;
        string sf;
        if(mp.find({p[0],s[s.size()-1]}) != mp.end()){
            for( auto c: mp[{p[0],s[s.size()-1]}] ){
                sf = c.second;
                if( ( p == sf.substr(0,p.size()) ) && ( s == sf.substr(sf.size()-s.size(), s.size()) ) ){
                    ans = c.first;
                    ms[{p,s}] = ans;
                    break;
            }
        }
        }
        return ans;
    }
};
