/*
        We are storing word as suffix + '{' + prefix 

        apple -> suffix + '{' + prefix, index
            apple{apple 0
            pple{apple 0
            ple{apple 0
            le{apple 0
            e{apple 0
            {apple 0


        */
class Node {
    Node *links[27] = {};   // 27 because we are using a special char '{' &&  index of  ['{'  -  'a']  is 26
    int index;
public:
    ~Node() {
        for(int i=0; i<27; i++) {
            if(links[i]) delete links[i];
        }
    }

    bool containsLetter(char ch) {
        return links[ch - 'a'] != nullptr;
    }

    void put(char ch, Node *newNode) {
        links[ch - 'a'] = newNode;
    }

    Node *get(char ch) {
        return links[ch - 'a'];
    }

    int getIndex() {
        return index;
    }

    void setIndex(int idx) {
        index = idx;
    }
};

class Trie {
    Node *root;
public:

    Trie() {
        root = new Node();
    }

    void insert(string s, int index) {
        Node *temp = root;
        for(auto ch: s) {
            if(temp->containsLetter(ch)) {
                temp = temp->get(ch);
            } else {
                temp->put(ch, new Node);
                temp =temp->get(ch);
            }
            temp->setIndex(index);
        }
    }

    int find(string s) {
        Node*temp = root;
        for(char ch:s) {
            if(temp->containsLetter(ch)) temp = temp->get(ch);

            else return -1;
        }
        return temp->getIndex();
    }
};


class WordFilter {
    Trie trie;
public:
    WordFilter(vector<string>& words) {

        for(int i=0;i<words.size();i++) {
            for(int j=0;j<words[i].size();j++) {
                string s = words[i].substr(j) + '{' + words[i];
                trie.insert(s, i);
            }
        }

    }

    int f(string prefix, string suffix) {
        string s = suffix + '{' + prefix;

        return trie.find(s);
    }
};

