    int find(string s) {
        Node*temp = root;
        for(char ch:s) {
            if(temp->containsLetter(ch)) temp = temp->get(ch);

            else return -1;
        }
        return temp->getIndex();
    }
};


class WordFilter {
    Trie trie;
public:
    WordFilter(vector<string>& words) {

        for(int i=0;i<words.size();i++) {
            for(int j=0;j<words[i].size();j++) {
                string s = words[i].substr(j) + '{' + words[i];
                trie.insert(s, i);
            }
        }

    }

    int f(string prefix, string suffix) {
        string s = suffix + '{' + prefix;

        return trie.find(s);
    }
};

