Implemented according to Approach #3: Trie of Suffix Wrapped Words
https://leetcode.com/problems/prefix-and-suffix-search/solution/

```
/*
 * Design a special dictionary with some words that 
 * searchs the words in it by a prefix and a suffix.
 * 
 * Implement the WordFilter class:
 * 
 * WordFilter(string[] words) Initializes the object 
 * with the words in the dictionary.
 * f(string prefix, string suffix) Returns the index of 
 * the word in the dictionary, which has the prefix prefix 
 * and the suffix suffix. If there is more than one valid index, 
 * return the largest of them. If there is no such word in the 
 * dictionary, return -1.
 *
 * 1 <= words.length <= 15000
 * 1 <= words[i].length <= 10
 * 1 <= prefix.length, suffix.length <= 10
 * words[i], prefix and suffix consist of lower-case English letters only.
 * At most 15000 calls will be made to the function f.
 */

typedef struct node {
    char chr;   /* for debug purpose */
    int idx;    /* dictionary index */
    struct node *next[27];   /* 'a' ~ 'z', and '#' */
} NODE;

int trie_init(NODE *trie)
{
    memset((void *)trie, 0, sizeof(NODE));
    return 0;
}

int trie_insert(NODE *trie, char *w, int idx)
{
    int pos;

    while (*w) {

        /* 'a' ~ 'z' or '#' */
        pos = (*w >= 'a' && *w <= 'z') ? (*w - 'a') : 26;

        /* Create new node if needed */
        if (trie->next[pos] == NULL) {
            trie->next[pos] = (NODE *)malloc(sizeof(NODE));
            trie = trie->next[pos];
            memset((void *)trie, 0, sizeof(NODE));
            trie->chr = *w;
            trie->idx = -1;
        }
        /* Move to the next corresponding node */
        else {
            trie = trie->next[pos];
        }

        /* Store the dictionary index if this is the last character of word */
        if (*(w + 1) == 0) {
            trie->idx = idx;
        }

        /* Continue to process next character */
        w++;
    }

    return 0;
}

/* 
 * DFS, traverse from given node, find out the maximum dictionary index 
 */
void trie_max_idx(NODE *trie, int *max_idx)
{
    if (trie == NULL) {
        return;
    }

    if (trie->idx != -1) {
        if (trie->idx > *max_idx) {
            *max_idx = trie->idx;
        }
    }

    for (int i = 0; i < 26; i++) {
        trie_max_idx(trie->next[i], max_idx);
    }
}

int trie_search(NODE *trie, char *w)
{
    int pos;
    int max_idx = -1;

    while(trie && *w) {

        pos = (*w >= 'a' && *w <= 'z') ? (*w - 'a') : 26;

        /* Mismatched */
        if (trie->next[pos] == NULL) {
            return -1;
        }
        /* 
         * Move to next character node 
         * 
         */
        else {
            trie = trie->next[pos];

            /* 
             * Found matching string, continue to travese 
             * rest nodes and find maximum index 
             */
            if (*(w + 1) == 0) {
                trie_max_idx(trie, &max_idx);
                return max_idx;
            }
        }

        w++;
    }

    return -1;
}

void trie_dfs_free(NODE *trie)
{
    if (trie == NULL) {
        return;
    }

    for (int i = 0; i < 27; i++) {
        trie_dfs_free(trie->next[i]);
    }

    for (int i = 0; i < 27; i++) {
        if (trie->next[i] != NULL) {
            free(trie->next[i]);
        }
    }
}

int trie_free(NODE *trie)
{
    trie_dfs_free(trie);
    return 0;
} 

typedef struct {
    NODE trie;
} WordFilter;


WordFilter* wordFilterCreate(char ** words, int wordsSize) {

    WordFilter *p_obj = (WordFilter *)calloc(sizeof(WordFilter), 1);
    char tmp[22];
    int len;

    trie_init(&p_obj->trie);

    for (int i = 0; i < wordsSize; i++) {

        /* "apple"  => longest suffix + prefix is "apple#apple" */
        len = strlen(words[i]);
        strcpy(tmp, words[i]);
        strcpy(&tmp[len], "#");
        strcpy(&tmp[len + 1], words[i]);

        /* "apple#apple", "pple#apple", "ple#apple", "le#apple", "e#apple", "#apple" */
        for (int j = 0; j <= len; j++) {
            trie_insert(&p_obj->trie, &tmp[j], i);
        }
    }

    return p_obj;
}

int wordFilterF(WordFilter* obj, char * prefix, char * suffix) {

    char tmp[22];
    int s_len = strlen(suffix);

    /* string = "suffix#prefix" */
    strcpy(tmp, suffix);
    strcpy(&tmp[s_len], "#");
    strcpy(&tmp[s_len + 1], prefix);

    return trie_search(&obj->trie, tmp);
}

void wordFilterFree(WordFilter* obj) {
    trie_free(&obj->trie);
    free(obj);
}

/**
 * Your WordFilter struct will be instantiated and called as such:
 * WordFilter* obj = wordFilterCreate(words, wordsSize);
 * int param_1 = wordFilterF(obj, prefix, suffix);

 * wordFilterFree(obj);
*/
```

