class WordFilter {
    Trie root;
    String[] words;

    public WordFilter(String[] words) {
        this.words = words;
        root = new Trie();

        Trie node = root;

        for(int i=0;i<words.length;i++){
            for(char ch : words[i].toCharArray()){
                if(root.trieArr[ch - 'a'] == null)
                    root.trieArr[ch - 'a'] = new Trie();
                root.trieArr[ch-'a'].indexList.add(i);
                root = root.trieArr[ch-'a'];
            }
            root = node;
        }

    }

    public int f(String prefix, String suffix) {
        Trie node = root;
        for(char c : prefix.toCharArray()){
            node = node.trieArr[c-'a'];
            if(node == null)
                return -1;
        }
        List<Integer> list= node.indexList;

        for(int i=list.size()-1;i>=0;i--){
            if(words[list.get(i)].endsWith(suffix)){
                return list.get(i);
            }
        }

        return -1;

    }

    private static class Trie{
        Trie[] trieArr = new Trie[26];
        List<Integer> indexList = new ArrayList<>();

    }
}

