class TrieNode:
    def __init__(self):
        self.children = defaultdict(TrieNode)
        self.index = set()

class WordFilter:

    def __init__(self, words: List[str]):
        self.prefix = TrieNode()
        self.suffix = TrieNode()

        for i, w in enumerate(words):
            suff = reversed(w)
            proot = self.prefix
            sroot = self.suffix

            for c in w:
                proot = proot.children[c]
                proot.index.add(i)

            for c in suff:
                sroot = sroot.children[c]
                sroot.index.add(i)

    @cache
    def f(self, prefix: str, suffix: str) -> int:
        proot = self.prefix
        sroot = self.suffix

        for c in prefix:
            if c in proot.children:
                proot = proot.children[c]
            else:
                return -1

        for c in reversed(suffix):
            if c in sroot.children:
                sroot = sroot.children[c]
            else:
                return -1

        ans = proot.index & sroot.index

        return max(ans)


