class WordFilter {
public:

    node* topRoot = NULL;
    node* bottomRoot = NULL;

    void insertTree1(string&s, int ind){

        node* temp = topRoot;
        for(char c: s){
            if(temp == NULL){
                topRoot = new node(ind);
                topRoot->ptr[c - 'a'] = new node(ind);
                temp = topRoot->ptr[c - 'a'];
            }
            else{
                if(temp->ptr[c - 'a'] == NULL){
                    temp->ptr[c - 'a'] = new node(ind);
                }
                temp = temp->ptr[c - 'a'];
                temp->vis.insert(ind);
            }
        }

        temp->ending = true;

    }

    void insertTree2(string s, int ind){

        reverse(s.begin(), s.end());

        node* temp = bottomRoot;
        for(char c: s){
            if(temp == NULL){
                bottomRoot = new node(ind);
                bottomRoot->ptr[c - 'a'] = new node(ind);
                temp = bottomRoot->ptr[c - 'a'];
            }
            else{
                if(temp->ptr[c - 'a'] == NULL){
                    temp->ptr[c - 'a'] = new node(ind);
                }
                temp = temp->ptr[c - 'a'];
                temp->vis.insert(ind);
            }
        }

        temp->ending = true;

    }

    WordFilter(vector<string>& words) {

        for(int i=0;i<words.size();i++){
            insertTree1(words[i], i);
        }

        for(int i=words.size()-1;i>=0;i--){
            insertTree2(words[i], i);
        }

    }

    set<int>& find1(string&s){

        node* temp = topRoot;
        set<int> t;

        for(char c: s){
            if(temp->ptr[c - 'a'] == NULL) return t;
            temp = temp->ptr[c - 'a'];
        }

        return temp->vis;
    }


    set<int>& find2(string&s){

        node* temp = bottomRoot;
        set<int> t;

        for(char c: s){
            if(temp->ptr[c - 'a'] == NULL) return t;
            temp = temp->ptr[c - 'a'];
        }

        return temp->vis;
    }

    int f(string prefix, string suffix) {
        reverse(suffix.begin(), suffix.end());

        auto&it1 = find1(prefix);
        auto&it2 = find2(suffix);

        for (auto a = it1.rbegin(); a != it1.rend(); a++){
            if(it2.find(*a) != it2.end()) return *a;
        }

        return -1;

    }
};
