from dataclasses import dataclass, field
from typing import List


@dataclass
class TrieNode:
    """Represents a character "node" with associated word indexes
    and the next available characters via a dict.
    """

    idxs: set = field(default_factory=set)
    next: dict = field(default_factory=dict)

    def add_ch_idx(self, char, idx):
        self.idxs.add(idx)

        if char in self.next:
            return self.next[char]

        else:
            tn = TrieNode()
            tn.idxs.add(idx) # adds idx to "leaf" nodes
            self.next[char] = tn
            return tn

    def __getitem__(self, ch):
        if ch in self.next:
            return self.next[ch]
        else:
            return False


class WordFilter:
    def __init__(self, words: List[str]):
        self.G_prefix = TrieNode()
        self.G_suffix = TrieNode()
        self.word_idxs = {}  # word to index

        idx = 0
        for word in words:
            if word not in self.word_idxs:
                self.word_idxs[word] = idx
                self.process_word(self.G_prefix, word, idx)
                self.process_word(self.G_suffix, reversed(word), idx)
            idx += 1

    def process_word(self, tn, word, idx):
        node = tn
        for c in word:
            node = node.add_ch_idx(c, idx)

    def f(self, prefix: str, suffix: str) -> int:
        node = self.G_prefix
        for c in prefix:
            if c in node.next:
                node = node[c]
            else:
                return -1

        node2 = self.G_suffix
        for c in suffix:
            if c in node2.next:
                node2 = node2[c]
            else:
                return -1

        return max(node.idxs & node2.idxs)
