//utility class to perform trie operations
class Trie{
private:
    TrieNode* root;

    void insert(string& word, int i){
        TrieNode* traveler =root;
        for(auto& c : word){
            if(traveler->charMap.find(c) == traveler->charMap.end()){
                traveler->charMap[c] = new TrieNode();
            }
            traveler = traveler->charMap[c];
        }
        traveler->isLeaf= true;
        traveler->index= i;
    }

    void collectAllWords(TrieNode* currentRoot, string& prefix, int& largest, string& suffix){
         TrieNode* traveler = currentRoot;
         if(!currentRoot){
             return;
         }

        if(currentRoot->isLeaf){
            if(matchSuffix(prefix, suffix)){
                largest = max(currentRoot->index, largest);
            }
        }

        for(auto& itr : currentRoot->charMap){
            prefix += itr.first;
            collectAllWords(itr.second, prefix, largest, suffix);
            prefix.pop_back();
        }
    }

    bool matchSuffix(string& word, string& suffix){
        int i = word.size()-1;
        int j = suffix.size()-1;
        //cout << a << " " << b << endl;
        while(i >= 0 && j >= 0 && word[i] == suffix[j]){
            i--;
            j--;
        }

        return j == -1;
    }

public:
    Trie(){
        root = new TrieNode();
    }

    ~Trie(){
        delete root;
    }

    void buildTrie(vector<string>& words){
        for(int i = 0; i < words.size(); i++){
            insert(words[i], i);
        }
    }

    int getWordIndex(string& prefix, string& suffix){
        TrieNode* traveler = root;
        //match prefix and traverse till node
        for(auto& c : prefix){
            if(traveler->charMap.find(c) == traveler->charMap.end()){
                return -1;
            }
