class WordFilter:

    def __init__(self, words: List[str]):
        self.pre_dict = defaultdict(list)
        self.su_dict = defaultdict(list)
        self.my_dict = defaultdict(int)

        for i, word in enumerate(words):
            cur_pre, cur_su = self.pre_dict, self.su_dict

            for w in word:
                if w not in cur_pre: cur_pre[w] = [{}, []]
                cur_pre[w][1].append(i)
                cur_pre = cur_pre[w][0]

            for w in word[::-1]:
                if w not in cur_su: cur_su[w] = [{}, []]
                cur_su[w][1].append(i)
                cur_su = cur_su[w][0]                

    def f(self, prefix: str, suffix: str) -> int:
        if prefix + " " + suffix in self.my_dict: return self.my_dict[prefix + " " + suffix]

        cur_pre, cur_su, res = self.pre_dict, self.su_dict, -1

        for c in prefix:
            if c not in cur_pre: return -1
            ls1, cur_pre = cur_pre[c][1], cur_pre[c][0]

        for c in suffix[::-1]:
            if c not in cur_su: return -1
            ls2, cur_su = cur_su[c][1], cur_su[c][0]

        if len(ls1) <= len(ls2): ls, se = ls1, set(ls2)
        else: ls, se = ls2, set(ls1)

        for l in ls[::-1]:
            if l in se: 
                res = l
                break

        self.my_dict[prefix + " " + suffix] = res

        return res

