/**
 * @param {string[]} words
 */
var WordFilter = function(words) {
    this.words = words;
    this.map = {};
};

/** 
 * @param {string} prefix 
 * @param {string} suffix
 * @return {number}
 */
WordFilter.prototype.f = function(prefix, suffix) {
    let index = -1;

    this.map[prefix] = this.map[prefix] || {};
    this.map[prefix][suffix] = this.map[prefix][suffix] || null;

    if (this.map[prefix][suffix] !== null) {
        return this.map[prefix][suffix];
    } 

	/*
	* In case of multiple matches, the highest index must be returned
	* Reverse interating the array so can stop the looping as soon as we match something.
	*/
    for (let i = this.words.length - 1; i >= 0; i--) {
        if (
			this.words[i].substring(0, prefix.length) === prefix &&
			this.words[i].substring(this.words[i].length - suffix.length) === suffix
		) {
            index = i;
            break;
        }
    }

	/*
	* Caching results that were already tested
	*/
    this.map[prefix][suffix] = index;
    return index;
};
