class WordFilter {
    class Trie {
        private Set<String> words;
        private Trie[] children;

        Trie() {
            children = new Trie[26];
            words = new HashSet<>();
        }

        private void add(String word, String value) {
            Trie root = this;

            for(char ch : word.toCharArray()) {
                int key = ch - 'a';

                if(root.children[key] == null) {
                    root.children[key] = new Trie();
                }

                root.children[key].words.add(value);
                root = root.children[key];
            }
        }

        private Set<String> search(String word) {
            Trie root = this;
            for(char ch : word.toCharArray()) {
                int key = ch - 'a';
                if(root.children[key] == null) {
                    return new HashSet<>();
                }

                root = root.children[key];
            }

            return root.words;
        }
    }

    private Trie prefixRoot;
    private Trie suffixRoot;

    private Map<String, Integer> indexMap;

    public WordFilter(String[] words) {
        prefixRoot = new Trie();
        suffixRoot = new Trie();

        indexMap = new HashMap<>();

        for(int i = 0; i < words.length; i++) {
            String word = words[i];

            indexMap.put(word, i);

            prefixRoot.add(word, word);

            String reverse = reverse(word);
            suffixRoot.add(reverse, word);
        }
    }

    private String reverse(String word) {
        char[] chars = word.toCharArray();

        int i = 0;
        int j = chars.length - 1;

        while(i < j) {
            char temp = chars[i];
            chars[i] = chars[j];
            chars[j] = temp;

            i++;
            j--;
        }

        return new String(chars).toString();
    }

    public int f(String prefix, String suffix) {
        Set<String> preRes = prefixRoot.search(prefix);
        Set<String> sufRes = suffixRoot.search(reverse(suffix));                

        int maxIndex = -1;

        for(String str : preRes) {
            if(sufRes.contains(str) && indexMap.get(str) > maxIndex) {
                maxIndex = indexMap.get(str);
            }
        }


        return maxIndex;
    }
}

