class WordFilter {
    class Trie {
        Trie[] children;
        boolean isWord;
        int weight;
        String word;

        Trie() {
            children = new Trie[26];
            isWord = false;
            weight = -1;
            word = "";
        }
    }

    Trie root;


    public WordFilter(String[] words) {
        root = new Trie();
        for (int i = 0; i < words.length; i++) {
            create(words[i], i);
        }
    }

    private int getIdx(char c) {
        return c - 'a';
    }

    private void create(String word, int w) {
        Trie cur = root;
        int i;
        for (char c : word.toCharArray()) {
            i = getIdx(c);
            if (cur.children[i] == null) {
                cur.children[i] = new Trie();
            }
            cur = cur.children[i];
        }
        cur.isWord = true;
        cur.weight = w;
        cur.word = word;
    }

    public int f(String prefix, String suffix) {
        Trie cur = root;
        for (char c : prefix.toCharArray()) {
            if (cur.children[getIdx(c)] == null) return -1;
            cur = cur.children[getIdx(c)];
        }
        return helper(cur, suffix, -1);
    }

    private int helper(Trie node, String suffix, int w) {
        int ans = w;
        if (node.isWord && node.word.endsWith(suffix)) {
            ans = Math.max(ans, node.weight);
        }
        for (Trie child : node.children) {
            if (child != null) {
                ans = Math.max(helper(child, suffix, ans), ans);
            }
        }
        return ans;
    }
}
