class WordFilter {
    unordered_map<string, int> weights;
    unordered_map<string, unordered_set<string>> byPrefix;
    unordered_map<string, unordered_set<string>> bySuffix;
public:
    WordFilter(vector<string>& words) {
        for (int i = 0; i < words.size(); ++i) {
            const string& word = words[i];
            weights[word] = i;
            byPrefix[""].insert(word);
            bySuffix[""].insert(word);
            for (int len = 1; len <= word.size(); ++len) {
                byPrefix[word.substr(0, len)].insert(word);
                bySuffix[word.substr(word.size() - len, len)].insert(word);
            }
        }
    }

    int f(string prefix, string suffix) {
        int weight = -1;
        if (byPrefix.count(prefix) > 0 && bySuffix.count(suffix) > 0) {
            for (const auto& word : byPrefix[prefix]) {
                if (bySuffix[suffix].count(word) == 1) {
                    int w = weights[word];
                    if (w > weight)
                        weight = w;
                }
            }
        }
        return weight;
    }
};
