        public int[] solve(int[] nums, int k) {
            int[] res = new int[k];
            int len = 0;
            for (int i = 0; i < nums.length; i++) {
                while (len > 0 && len + nums.length - i > k && res[len - 1] < nums[i]) {
                    len--;
                }
                if (len < k)
                    res[len++] = nums[i];
            }
            return res;
        } }


