class Solution:

    def maxNumber(self, nums1: List[int], nums2: List[int], k: int) -> List[int]:

        def calcnext(a):
            ret = [[-1] * 10 for i in range(len(a) + 1)]
            for i in reversed(range(len(a))):
                for d in range(0, 10):
                    ret[i][d] = i if a[i] == d else ret[i + 1][d]
            return ret

        M, N = len(nums1), len(nums2)
        next1 = calcnext(nums1)
        next2 = calcnext(nums2)
        ret = []
        S = [(0, 0)]

        for i in range(k):
            rem = k - i - 1
            found, best = False, [N + 1] * (M + 1)
            for d in reversed(range(0, 10)):
                for (d1, d2) in S:
                    n1 = next1[d1][d] + 1
                    if n1 and M - n1 + N - d2 >= rem:
                        found, best[n1] = True, min(best[n1], d2)
                    n2 = next2[d2][d] + 1
                    if n2 and M - d1 + N - n2 >= rem:
                        found, best[d1] = True, min(best[d1], n2)
                if found:
                    ret += [d]
                    break
            S = [(i, best[i]) for i, x in enumerate(best) if best[i] <= N]

        return ret

