class Solution {
public:
    // pick k numbers from vector num
    vector<int> select(vector<int>& nums, int k){
        int n = nums.size();
        vector<int> res(k, INT_MIN);
        for(int i = 0, j = 0;i<nums.size();i++){
            int x = nums[i];
            // monotonic stack
            while(j>0&&x > res[j-1]&&i+k-j<n) j--;
            if(j<k) res[j++] = x;
        }
        return res;
    }
    vector<int> merge(vector<int>& a, vector<int> &b){
        vector<int> c;
        while(a.size()&&b.size()){
            // The time complexity of compare operation for two vector is order of n.
            // Greedy Algorithm:
            // If we meet two elements which have the same value, choose the vector which has the higher dictionary order first.
            // 1: ...xxxb , 2: ...xxxa, if a > b, then merge sequence 2 first. 
            if(a > b)
                c.push_back(a[0]), a.erase(a.begin());
            else
                c.push_back(b[0]), b.erase(b.begin());
        }
        while(a.size())
            c.push_back(a[0]), a.erase(a.begin());
        while(b.size())
            c.push_back(b[0]), b.erase(b.begin());
        return c;
    }
    vector<int> maxNumber(vector<int>& nums1, vector<int>& nums2, int k) {
        vector<int> res(k, INT_MIN);
        int n = nums1.size(), m = nums2.size();
        // s1  => nums1.size(), s2 => nums2.size()
        // {0<=i<=k, i<=s1, 0<=k-i<=s2} => i>=max(0, k-s2), i<=min(k, n)
        for(int i = max(0, k-m);i<=min(k, n);i++){
            vector<int> a = select(nums1, i);
            vector<int> b = select(nums2, k - i);
            res = max(res, merge(a, b));
        }
        return res;
    }
};
