class Solution {
public:
    vector<int> calc(vector<int> &v,int k,int n) {
        vector<int> v1;
        // IF I replace the current top or not.
        int i=-1,j,ind=-1,ans=-1;
        while(k) {
            j=i+1;ind=-1;ans=-1;
            while(j<n&&n-j>=k) {
                if(v[j]>ans) {
                    ans=v[j];
                    ind=j;
                }
                j++;
            }
            i=ind;
            v1.push_back(ans);
            k--;
        }
        return v1;
    }
    vector<int> merge(vector<int> &v1,vector<int> &v2,int n,int m) {
        vector<int> v;
        if(n>m) {
            swap(v1,v2);
            swap(n,m);
        }
        int i=0,j=0;
        while(i<n && j<m) {
            if(v1[i]>v2[j]) {
                v.push_back(v1[i]);
                i++;
            }
            else if(v1[i]<v2[j]) {
                v.push_back(v2[j]);
                j++;
            }
            else {
                int k=i+1,l=j+1,f=1;
                while(k<n && l<m) {
                    if(v1[k]>v2[l]) { f=0;break; }
                    else if(v1[k]<v2[l]) { f=1;break;}
                    k++;
                    l++;
                }

                if(f==0||l==m) { v.push_back(v1[i++]); }
                else     { v.push_back(v2[j++]); }
            }
        }
        while(i<n) { v.push_back(v1[i]);i++; }
        while(j<m) { v.push_back(v2[j]);j++; }
        return v;
    }
    int comp(vector<int> v1,vector<int> v2,int k) {
        for(int i=0;i<k;i++) { 
            if(v1[i]!=v2[i]) return v1[i]>v2[i];
        }
        return 0;
    }
    vector<int> maxNumber(vector<int>& nums1, vector<int>& nums2, int k) {
        int n=nums1.size(),m=nums2.size();
        // If I have to chose k greatest elements.
        // from a vector. how you are going to pick up.
        // this task is awesome.
        vector<int> ans(k,-1);
        for(int i=0;i<=min(n,k);i++) {
            int r=k-i;
            if(r<=m) { 
                vector<int> v1=calc(nums1,i,n),v2=calc(nums2,r,m);
                vector<int> x=merge(v1,v2,i,r);
                if(comp(x,ans,k)>0) ans=x;
            }
        }
        return ans;
    }
};

