class Solution {
public:
    vector<int> ans;
    vector<int> maxNumber(vector<int>& a, vector<int>& b, int k) {
          for(int i=0; i<=k; i++) {
                vector<int> s1 , s2;
                if(i>0) s1 = getBest(a,i);  // make i length lexographically biggest sequence possible from stack 1 
                if(k-i>0) s2 = getBest(b,k-i); // make k-i length lexographically biggest sequence from stack 2
                vector<int> s3;
                int m = 0 , n = 0;

                // take the element from corresponding stack is is lexographically bigger!
                while(m<s1.size() && n<s2.size()) s3.push_back((cmp(s1,s2,m,n) ? s1[m++] : s2[n++]));

                while(m<s1.size()) s3.push_back(s1[m++]);
                while(n<s2.size()) s3.push_back(s2[n++]);

                if(s3.size()==k) ans=max(ans,s3);
          }
         return ans;
    }
    bool cmp(vector<int>&a,vector<int>&b,int i,int j) {
         while(i<a.size() && j<b.size() && a[i]==b[j]) i++,j++;
         return (j==b.size() || ((i<(int)a.size()) && (a[i] > b[j])));
    }
    vector<int> getBest(vector<int> &a,int k) {
        vector<int> stk;
        int n = a.size();
        for(int i=0; i<a.size(); i++) {
             // a bigger element is present than current maximum & you can make a sequence >= k !
             while(!stk.empty() && stk.back() < a[i] && (((int)stk.size() - 1) + (n - i)) >=k) stk.pop_back();
             stk.push_back(a[i]);
        }
        while(stk.size() > k) stk.pop_back();
        return stk;
    }
};
