class Solution {
public:
    vector<int> maxNumber(vector<int>& nums1, vector<int>& nums2, int k) {
        vector<int> ans;
        const int m = nums1.size(), n = nums2.size();

        for (int i = 0; i <= k; i++) {
            int j = k - i;  
            if (i > m || j > n) continue;
            ans = max(ans, maxNumber(maxNumber(nums1,i),maxNumber(nums2,j)));
        }

        return ans;
    }
private:

    vector<int> maxNumber(const vector<int>& nums, int k) {
        if (k == 0) return {};
        int to_pop = nums.size() - k;
        vector<int> ans;
        for (auto &num : nums) {
            while (!ans.empty() && ans.back() < num && 0 < to_pop--)
                 ans.pop_back();
            ans.push_back(num);
        }
        ans.resize(k);
        return ans;
    }

     vector<int> maxNumber(const vector<int>& nums1,const vector<int>& nums2) {
        vector<int> ans;
        auto s1 = nums1.begin();
        auto e1 = nums1.end();
        auto s2 = nums2.begin();
        auto e2 = nums2.end();
        while (s1 != e1 || s2 != e2) {
            ans.push_back(lexicographical_compare(s1,e1,s2,e2)?*s2++:*s1++);
        }
         return ans;
     }

};
