class Solution:
    def maxNumber(self, nums1: List[int], nums2: List[int], k: int) -> List[int]:

        def get_max_subarray(nums, k):
            stack = []
            drop = len(nums) - k
            for num in nums:
                while drop and stack and stack[-1] < num:
                    stack.pop()
                    drop -= 1
                stack.append(num)
            return stack[:k]

        max_num = []

        for i in range(max(0, k - len(nums2)), min(k, len(nums1)) + 1):
            max_subarray1 = get_max_subarray(nums1, i)
            max_subarray2 = get_max_subarray(nums2, k - i)

            candidate = []
            while max_subarray1 or max_subarray2:
                if max_subarray1 > max_subarray2:
                    candidate.append(max_subarray1.pop(0))
                else:
                    candidate.append(max_subarray2.pop(0))

            if candidate > max_num:
                max_num = candidate

        return max_num
