class Solution {
public:
    vector<int> maxNumber(vector<int>& nums1, vector<int>& nums2, int k) {
        vector<int> ans(k,0);

        int n1 = nums1.size();
        int n2 = nums2.size();
        vector<vector<int>> ind1(n1+1);  // direct pointer for each digit for every ix
        vector<vector<int>> ind2(n2+1);  // direct pointer for each digit for every ix
        vector<int> il(10,n1);
        ind1[n1] = il;
        int i;
        for(i=n1-1; i>=0; i--) 
        {  // Starting from the end, update the index for the next for each digit
            il[nums1[i]]=i;
            ind1[i] = il;
        }
        vector<int> ik(10,n2);
        ind2[n2] = ik;
        for(i=n2-1; i>=0; i--) 
        {  // Starting from the end, update the index for the next for each digit
            ik[nums2[i]]=i;
            ind2[i]=ik;
        }
        int ix = 0;
        queue<pair<int,int>> q1;  // Double queues system to implement BFS
        queue<pair<int,int>> q2;
        queue<pair<int,int>> &q = q1;        // Current queue
        queue<pair<int,int>> &q_next = q2;   // Next queue
        q.push({0,0});
		unordered_map<int,int> hash;         // Hash to avoid duplication
        while (ix < k)
        {
            while(!q.empty())
            {
                int ix1 = q.front().first;
                int ix2 = q.front().second;
                q.pop();
                if (hash.count(ix1*512+ix2)) continue;  // Avoid duplication
                hash[ix1*512+ix2]++;
                for(int d = 9; d>=ans[ix]; d--)
                {   // Checking the availebility from high to low (the current highest for this ix)
                    int i =ind1[ix1][d];
                    if (i<n1 && n1+n2-ix2-i>=k-ix)    
                    {
                        // cout << d << ", " << i << endl;
                         if (ans[ix] < d) 
                        {  // if better than prev (same ix) clear prev
                            while(!q_next.empty()) q_next.pop();
                            ans[ix] = d;
                        }
                        q_next.push({i+1,ix2});  // add curent combination
                        break;
                    }
                }
                for(int d = 9; d>=ans[ix]; d--) 
                {   // Checking the availebility from high to low (the current highest for this ix)
                    int i =ind2[ix2][d];    // Update the ix for next d (n2 means no next available)

                    if (i<n2 && n1+n2-ix1-i>=k-ix)  // Check if this ix allow leave enough digit to get to length k
                    {  // Yes - 
                        // cout << d << ",," << i << endl;
                        if (ans[ix] < d)  // Check if new max for this ix. 
                        {   // If new max arase acumulated combination of the old max
                            while(!q_next.empty()) q_next.pop();
                            ans[ix] = d;  // Save new max
                        }
                        q_next.push({ix1,i+1});  //\xA0Push current combination
                        break;
                    }
                }
            }
            swap(q,q_next);  // prepare next queue  (next become current)
            ix++;
        }
        return  ans;   
    }
};

