class Solution
{
public:
    void print(vector<int> & res){
        for(int x : res)
            cout << x << " ";
        cout << endl;
    }

    vector<int> findMax(vector<int> &nums, int k)
    {
        vector<int> res;
        int n = nums.size();
        for (int i = 0; i < n; i++)
        {
            int m = res.size();
            while (m && res.back() < nums[i])
            {
                if (((n - i) + m - 1) >= k)
                {
                    res.pop_back();
                    m--;
                }
                else
                    break;
            }
            if (m < k)
            {
                m++;
                res.push_back(nums[i]);
            }
        }
        return res;
    }

    vector<int> compare(vector<int> &nums1, vector<int> &nums2)
    {
        int n = nums1.size();
        int m = nums2.size();
        if (n > m)
            return nums1;
        if (m > n)
            return nums2;
        for (int i = 0; i < n; i++)
        {
            if (nums1[i] > nums2[i])
                return nums1;
            else if (nums1[i] < nums2[i])
                return nums2;
        }
        return nums1;
    }
    vector<int> merge(vector<int> &nums1, vector<int> &nums2)
    {
        vector<int> res;
        int n = nums1.size();
        int m = nums2.size();
        int nP = 0;
        int mP = 0;
        while (nP < n && mP < m)
        {
              if(nums1[nP] == nums2[mP]){  
                  int i = nP;  
                  int j = mP;

                  while( i < n && j < m && nums1[i] == nums2[j]){
                      i++;
                      j++;
                  } 

                  if(i == n)
                      res.push_back(nums2[mP++]);

                  else if ( j == m)
                      res.push_back(nums1[nP++]);

                  else if(nums1[i] > nums2[j])
                     res.push_back(nums1[nP++]); 

                  else
                     res.push_back(nums2[mP++]); 

                continue; 
          } 
          if (nums1[nP] > nums2[mP])
                res.push_back(nums1[nP++]);
           else if(nums2[mP] > nums1[nP]) 
                res.push_back(nums2[mP++]);
        }
        while (nP < n)
            res.push_back(nums1[nP++]);
        while (mP < m)
            res.push_back(nums2[mP++]);
        return res;
    }

    vector<int> maxNumber(vector<int> &nums1, vector<int> &nums2, int k)
    {
        vector<int> ans;
        for (int i = 0; i < k; i++)
        {
            auto first = findMax(nums1, k - i);
            auto second = findMax(nums2, i);
            auto tmp = merge(first, second);
            ans = compare(ans, tmp);
        }
        return ans;
    }
};
