class Solution {
public:
    vector<string> single_array(vector<int>& arr1,int k)
    {
        int n = arr1.size();
        vector<vector<string>> dp(min(n,k),vector<string>(n,""));//i+1 digits and ending at j maintaining ordering
        vector<string> dp1(min(n,k),""); //max i-digit number formed maintaing ordering

        for(int j=0;j<n;j++)
        {
            dp[0][j].push_back((char)(arr1[j]+'0'));
            dp1[0] = max(dp1[0],dp[0][j]);
            if(j>0) dp[0][j] = max(dp[0][j],dp[0][j-1]);
        }

        for(int i=1;i<dp1.size();i++)
        {
            // num of digits required is i+1
            // we start at i, not before that
            for(int j=i;j<n;j++)
            {
                dp[i][j] = max(dp[i][j],dp[i-1][j-1]+(char)(arr1[j]+'0'));
                dp1[i] = max(dp[i][j],dp1[i]);
                if(j>i) dp[i][j] = max(dp[i][j],dp[i][j-1]); 
                // beware : max on strings works as expected when both strings are of same size
            }
        }
        return dp1;

    }

    string merge(string r1,string r2)
    {
        string fin = "";
        int n = r1.size();
        int m = r2.size();
        int p1 = 0;
        int p2 = 0;
        while(p1<n && p2<m)
        {
            if(r1[p1]>r2[p2])
            {
                fin.push_back(r1[p1]);
                p1++;
            }
            else if(r1[p1]<r2[p2])
            {
                fin.push_back(r2[p2]);
                p2++;
            }
            else
            {
                if(r1.substr(p1)>=r2.substr(p2))
                {
                    fin.push_back(r1[p1]);
                    p1++;
                }
                else
                {
                    fin.push_back(r2[p2]);
                    p2++;
                }
            }
        }
        while(p1<n) 
        {
            fin.push_back(r1[p1]);p1++;
        }
        while(p2<m) 
        {
            fin.push_back(r2[p2]);p2++;
        }
        return fin;
    }

    vector<int> maxNumber(vector<int>& arr1, vector<int>& arr2, int k) 
    {
        vector<int> ans;
        int n = arr1.size();
        int m = arr2.size();
        if(n==0 && m==0) return ans;
        if(k==0) return ans;

