class Solution {
    public int[] maxNumber(int[] A, int[] B, int k) {
        if(A.length==0)return get(B,k,true);
        if(B.length==0)return get(A,k,true);
        int res[]=new int[k];
        for(int i=0;i<k;i++){
            int A1[]=get(A,i,false);
            int A2[]=get(B,k-i,false);
            if(A1==null||A2==null)continue;
            int newA[]=merge(A1,A2);
            if(compare(res,newA)){
                res=newA;
            }
        }
        return res;
    }

    public boolean compare(int A[],int B[]){
        for(int i=0;i<A.length;i++){
            if(B[i]==A[i])continue;
            if(B[i]>A[i])return true;
            else return false;
        }
        return false;
    }

    public int[] merge(int A[],int B[]){
        int res[]=new int[A.length+B.length-2];
        int i=0,j=0,k=0;
        while(i<A.length-1&&j<B.length-1){
            if(A[i]>B[j]){
                res[k++]=A[i++];
            }else if(B[j]>A[i]){
                res[k++]=B[j++];
            }else{//equal
                int ii=i,jj=j;
                int prei=i,prej=j;
                while(ii<A.length-1&&jj<B.length-1){
                    while(ii<A.length-1&&A[ii]==A[prei])ii++;
                    while(jj<B.length-1&&B[jj]==B[prej])jj++;
                    if(ii<A.length-1&&jj<B.length-1&&A[ii]==B[jj]&&(ii-i==jj-j)){
                        prei=ii;prej=jj;
                        continue;
                    }
                    else break;
                }
                if(ii-i==jj-j){
                    if(A[ii]>B[jj])res[k++]=A[i++];
                    else res[k++]=B[j++];
                }else if(ii-i<jj-j){
                    if(A[ii]>B[prej])res[k++]=A[i++];
                    else res[k++]=B[j++];
                }else{
                   if(B[jj]>A[prei])res[k++]=B[j++];
                    else res[k++]=A[i++]; 
                }

            }
        }
        while(i<A.length-1)res[k++]=A[i++];
        while(j<B.length-1)res[k++]=B[j++];
        return res;
    }

    public void print(int A[]){
        for(int i:A)System.out.print(i+"  ");
        System.out.println();
    }

    public int[] get(int A[],int k,boolean flag){
        if(k>A.length)return null;
        Stack<Integer>stack=new Stack<>();
        int res[]=new int[k];
        for(int i=0;i<A.length;i++){
            int remain=A.length-(i+1);
            while(stack.size()!=0&&A[i]>stack.peek()&&stack.size()+remain>=k)stack.pop();
            stack.push(A[i]);
        }
        int index=res.length-1;
        while(stack.size()>k)stack.pop();
        while(stack.size()!=0){
            res[index--]=stack.pop();
        }
        if(flag)return res;
        int ans[]=new int[res.length+1];
        for(int i=0;i<res.length;i++)ans[i]=res[i];
        ans[ans.length-1]=Integer.MIN_VALUE;
        return ans;
    }
}

