class Solution {
public:
    bool check(string&str1 , string&str2 , int i , int j){
        while(i < str1.size() || j < str2.size()){
            if(i >= str1.size()) return false;
            else if(j >= str2.size())return true;
            else if(str1[i] < str2[j])return false;
            else if(str1[i] > str2[j])return true;
            else {
                i++;
                j++;
            }
        }
        return true;
    }
    string mergeSort(string &str1 , string str2){
        int n = str1.size();
        int m = str2.size();

        string result = "";
        int i = 0 , j = 0;
        while(i < n && j < m){
            if(str1[i]==str2[j]){
                if(check(str1 , str2 , i + 1 , j + 1)==true){
                    result+=str1[i++];
                }else{
                    result+=str2[j++];
                }
            }
            else if(str1[i] > str2[j]){
                result += str1[i];
                i++;
            }else{
                result+=str2[j];
                j++;
            }
        }
        while(i < n){
            result += str1[i++];
        }
        while(j < m){
            result += str2[j++];
        }
        return result;
    }
    string findMaxK(vector<int>&nums , int k){
        int n = nums.size();
        int toRemove = n - k;
        stack<int> st;
        st.push(nums[0]);
        for(int i = 1 ; i < n ; i++){
            int number = nums[i];
            while(st.size() && st.top()<number && toRemove>0){
                toRemove--;
                st.pop();
            }
            st.push(number);
        }
        while(toRemove>0){
            st.pop();
            toRemove--;
        }
        string result = "";
        while(st.size()){
            result += (st.top() + '0');
            st.pop();
        }
        reverse(result.begin() , result.end());
        return result;
    }
    vector<int> maxNumber(vector<int>& nums1, vector<int>& nums2, int k) {
        string result(k , '0');
        int n = nums1.size();
        int m = nums2.size();
        for(int i = 0 ; i <= k ; i++){
            int fromFirst = i;
            int fromSecond = k - i;
            if(fromFirst > n || fromSecond > m)continue;
            string first = findMaxK(nums1 , fromFirst);
            string second = findMaxK(nums2 , fromSecond);
            string answer = mergeSort(first , second);
            result = max(result , answer);
            cout<<result<<endl;
        }
        cout<<findMaxK(nums1 , 1)<<endl;
        vector<int> answer;
        for(auto ch : result)answer.push_back(ch - '0');
        return answer;
    }
};
