class Solution {
public:
    vector<int> maxNumber(vector<int>& v1, vector<int>& v2, int k) {
        int n = v1.size(), m = v2.size();
        // 1st wale se i, le rha , second se k - i
        vector<int> chk;
        for(int i=0;i<k;i++){
            chk.push_back(0);
        }
        for(int i=0;i<=n and k-i>=0;i++){
            int fstNeed = i, secNeed = k - i;
            int fstRem = n - fstNeed, secRem = m - secNeed;
            vector<int> ans;
            if(secRem < 0) continue;
            stack<int> s;
            vector<int> fst;
            for(int j=0;j<n;j++){
                while(s.size() > 0 and v1[j] > s.top() and fstRem){
                    s.pop();
                    fstRem --;
                }
                s.push(v1[j]);
            }
            while(fstRem --) s.pop();
            while(s.size() > 0) fst.push_back(s.top()), s.pop();
            reverse(fst.begin(), fst.end());
            vector<int> sec;
            for(int j=0;j<m;j++){
                while(s.size() > 0 and v2[j] > s.top() and secRem){
                    s.pop(); secRem --;
                }
                s.push(v2[j]);
            }
            while(secRem --) s.pop();
            while(s.size() > 0) sec.push_back(s.top()), s.pop();
            reverse(sec.begin(), sec.end());
            if(sec.size() == 0 or fst.size() == 0){
                if(sec.size() == 0) ans = fst;
                else ans = sec;
            }
            else{
                int k1 = 0, k2 = 0;
                while(k1 < ((int)fst.size()) and k2 < ((int)sec.size())){
                    if(fst[k1] > sec[k2]){
                        ans.push_back(fst[k1]);
                        k1++;
                    }
                    else if(sec[k2] > fst[k1]){
                        ans.push_back(sec[k2]);
                        k2++;
                    }
                    else{
                        // equal wala scene
                        int vp = -1;
                        for(int l=0;;l++){
                            if(k1+l >= fst.size()){
                                ans.push_back(sec[k2]), k2++;
                                break;
                            }
                            if(k2+l >= sec.size()){
                                ans.push_back(fst[k1]), k1++;
                                break;
                            }
                            if(fst[k1+l] > sec[k2+l]){
                                vp = 1;
                                break;
                            }
                            else if(fst[k1+l] < sec[k2+l]){
                                vp = 0;
                                break;
                            }
                        }
                        if(vp == 1) ans.push_back(fst[k1]), k1++;
                        else if(vp == 0) ans.push_back(sec[k2]), k2++;
                    }
                }
                for(int j=k1;j<fst.size();j++){
                    ans.push_back(fst[j]);
                }
                for(int j=k2;j<sec.size();j++){
                    ans.push_back(sec[j]);
                }
            }
            if(ans > chk){
                chk = ans;
            }
        }
        return chk;
    }
};
