class Solution {
public:
    struct Comb {
        int i;
        int j;
        int lastNum;
        Comb(){
            i=0;
            j=0;
        }
    };
    vector<int> maxNumber(vector<int>& nums1, vector<int>& nums2, int k) {
        int n = nums1.size();
        int m = nums2.size();
        vector<int> res;
        queue<Comb> q;
        Comb cmb;
        q.push(cmb);
        vector<vector<bool>> done(n+1, vector<bool>(m+1, false));
        while(!q.empty()){
            int siz = q.size();
            vector<Comb> combStk;
            int mxLastVal = 0;

            while(siz--){
                Comb top = q.front();
                q.pop();
                if (done[top.i][top.j]) continue;
                done[top.i][top.j] = true;
                int rem = k - res.size();
                int tot = (n-top.i) + (m-top.j);

                int iMax = min(tot-rem+top.i, n-1);
                int jMax = min(tot-rem+top.j, m-1);

                int imxIdx = top.i;
                int jmxIdx = top.j;

                for(int i=top.i; i<= iMax; i++){
                    if (nums1[i] > nums1[imxIdx]) imxIdx = i;
                }
                for(int j=top.j; j<= jMax; j++){
                    if (nums2[j] > nums2[jmxIdx]) jmxIdx = j;
                }

                if ( (imxIdx <= iMax && jmxIdx > jMax) ||  ( imxIdx <= iMax && nums1[imxIdx] >= nums2[jmxIdx]) ){
                    Comb iChild;
                    iChild.lastNum = nums1[imxIdx];
                    iChild.i = imxIdx+1;
                    iChild.j = top.j;
                    combStk.push_back(iChild);
                    mxLastVal = max(mxLastVal, nums1[imxIdx]);
                }
                if ( (jmxIdx <= jMax && imxIdx > iMax) ||   ( jmxIdx <= jMax &&  nums1[imxIdx] <= nums2[jmxIdx]) ){
                    Comb jChild;
                    jChild.lastNum = nums2[jmxIdx];
                    jChild.i = top.i;
                    jChild.j = jmxIdx+1;
                    combStk.push_back(jChild);
                    mxLastVal = max(mxLastVal, nums2[jmxIdx]);
                }

            }
            res.push_back(mxLastVal);

            if (k == res.size()) return res;
            for(Comb comb : combStk){
                if (comb.lastNum < mxLastVal) continue;
                q.push(comb);
            }

        }
        return res;
    }
};
