var maxNumber = function (a, b, k) {
    let m = a.length,
        n = b.length,
        ss = Math.max(0, k - n),
        ee = Math.min(k, m),
        maxSubsequence = new Array(k).fill(0);
    for (let i = ss; i <= ee; i++) {
        let ss1 = new MaxSubsequence(a, i);
        let ss2 = new MaxSubsequence(b, k - i);
        let curMaxSubsequence = merge(ss1, ss2);
        if (compare(curMaxSubsequence, 0, maxSubsequence, 0) > 0) {
            maxSubsequence.splice(0, k, ...curMaxSubsequence);
        }
    }
    return maxSubsequence;
};

var MaxSubsequence = function (a, mustTakeOptions) {
    let n = a.length;

    let canThrowAwayOptions = n - mustTakeOptions;
    let st = [];
    let top = -1;
    for (let e of a) {
        if (
            top + 1 < mustTakeOptions || //
            (canThrowAwayOptions && st[top] < e)
        ) {
            while (canThrowAwayOptions && st[top] < e) {
                top--;
                canThrowAwayOptions--;
            }
            top++;
            st[top] = e;
        } else {
            canThrowAwayOptions--;
        }
    }
    return st;
};

var merge = (a, b) => {
    let m = a.length;
    let n = b.length;
    let o = m + n;

    let c = new Array(o);
    let i = 0;
    let j = 0;
    for (let k = 0; k < o; k++) {
        if (compare(a, i, b, j) > 0) {
            c[k] = a[i];
            i++;
        } else {
            c[k] = b[j];
            j++;
        }
    }
    return c;
};

var compare = (a, i, b, j) => {
    let m = a.length;
    let n = b.length;
    while (i < m && j < n) {
        if (a[i] !== b[j]) {
            return a[i] - b[j];
        }
        i++;
        j++;
    }
    return m - i - (n - j);
};
