class Solution {
private:
    vector<int> construct(const vector<int>& nums){
        int sz = nums.size();
        vector<int> ret(sz, sz);
        vector<int> pos(10, sz);
        for(int i = sz-1; i >= 0; i--){
            for(int j = nums[i]+1; j <= 9; j++) ret[i] = min(ret[i], pos[j]);
            pos[nums[i]] = i;
        }
        return ret;
    }

    int next_pos(const vector<int>& larger_pos, int p, int len, int remain_len){
        while(remain_len > 0 && len-larger_pos[p] >= remain_len) p= larger_pos[p];
        return p;
    }

    int get_x(const vector<int>& nums, int p, int remain_len){
        if(remain_len <=0 || p >= nums.size()) return -1;
        return nums[p];
    }

    void explore(const vector<int>& nums1, const vector<int>& nums2,const vector<int>& larger_pos1, const vector<int>& larger_pos2, vector<int>& ret, int len1, int len2) {
        int p1 = 0; int p2 = 0;
        int sz1 = nums1.size();
        int sz2 = nums2.size();
        bool is_larger = false;
        p1= next_pos(larger_pos1, p1, sz1, len1);
        p2= next_pos(larger_pos2, p2, sz2, len2);
        for(int i = 0; i < k; ++i){
            int x1 = len1? nums1[p1] : -1;
            int x2 = len2? nums2[p2] : -1;
            if((!is_larger) && ret[i] > max(x1, x2)) return;
            int iter = 1; int nxp1 = p1; int nxp2 = p2;
            int x1_ =  0; int x2_ = 0;
            while(x1_ == x2_ && x1_ != -1){
                nxp1 = next_pos(larger_pos1, nxp1+1, sz1, len1-iter);
                nxp2 = next_pos(larger_pos2, nxp2+1, sz2, len2-iter);
                x1_ = get_x(nums1, nxp1, len1-iter);
                x2_ = get_x(nums2, nxp2, len2-iter);
                iter++;
            }
            if(x1 > x2 || (x1==x2&&(x1_ > x2_))){
                p1 = next_pos(larger_pos1, p1+1, sz1, len1-1);
                len1--;
            } else {
                p2 = next_pos(larger_pos2, p2+1, sz2, len2-1);
                len2--;
            }
            if(ret[i] < max(x1, x2)) is_larger = true;
            ret[i] = max(x1, x2);
        }
    }

public:
    vector<int> maxNumber(vector<int>& nums1, vector<int>& nums2, int k_) {
        int sz1 = nums1.size();
        int sz2 = nums2.size();
        k = k_;
        vector<int> ret(k);
        auto larger_pos1 = construct(nums1);
        auto larger_pos2 = construct(nums2);

        for(int i = max(k-sz2, 0); i <= min(sz1, k); ++i){
            explore(nums1, nums2, larger_pos1, larger_pos2, ret, i, k-i);
        }

        return ret;
    }

int k;
};
