        int len=0,pos=0;
        while(len!=len1){
            pos=d1[pos][num1.size()-1-len1+len],a[len]=num1[pos],len++,pos++;
        }
        len=0,pos=0;
        while(len!=len2){
            pos=d2[pos][num2.size()-1-len2+len],b[len]=num2[pos],len++,pos++;
        }
        bool equal=true;
        for(int i=0,j=0,k=0;i<len1+len2;i++){
            if(a[j]>b[k]){
                if(ans[i]>a[j]&&equal) return;
                if(ans[i]!=a[j]) equal=false;
                ans[i]=a[j],j++;
            }
            else if(b[k]>a[j]){
                if(ans[i]>b[k]&&equal) return;
                if(ans[i]!=b[k]) equal=false;
                ans[i]=b[k],k++;
            }
            else{
                bool ct=true;
                if(ans[i]>a[j]&&equal) return;
                int x=j,y=k;
                while(1){
                    if(a[x]>b[y]){
                        ct=true;
                        break;
                    } 
                    if(b[y]>a[x]){
                        ct=false;
                        break;
                    }
                    if(x==len1&&y==len2){
                        ct=true;
                        break;
                    }
                    x++,y++;
                }
                if(ct){
                    if(ans[i]!=a[j]) equal=false;
                    ans[i]=a[j],j++;
                }
                else{
                    if(ans[i]!=b[k]) equal=false;
                    ans[i]=b[k],k++;
                }
            }
        }
    }
    vector<int> maxNumber(vector<int>& nums1, vector<int>& nums2, int k) {
        int n=nums1.size(),m=nums2.size();
