        nums1.push_back(-1),nums2.push_back(-1);
        d1.resize(n,vector<int>(n,n));
        d2.resize(m,vector<int>(m,m));
        d1[0][0]=0;
        for(int i=1;i<n;i++){
            d1[0][i]=d1[0][i-1];
            if(nums1[i]>nums1[d1[0][i-1]]) d1[0][i]=i;
        }
        for(int i=1;i<n;i++)
            for(int j=i;j<n;j++){
                d1[i][j]=d1[i][j-1];
                if(nums1[j]>nums1[d1[i][j-1]]) d1[i][j]=j;
            }
        d2[0][0]=0;
        for(int i=1;i<m;i++){
            d2[0][i]=d2[0][i-1];
            if(nums2[i]>nums2[d2[0][i-1]]) d2[0][i]=i;
        }
        for(int i=1;i<m;i++)
