class Solution {
    int k, h = 0;
    public int[] maxNumber(int[] nums1, int[] nums2, int k) {
        int [] result = new int[k];
        this.k = k;
        if(k > nums1.length + nums2.length){
            return null;
        }
        String resultValue = getMaxNumber(nums1, nums2, 0);

        int i = 0;
        while(i < this.k){
            result[i] = resultValue.charAt(i++) - '0';
        }
        return result;

    }

    public String getMaxNumber(int[] nums1, int[] nums2, int h){
        StringBuilder result = new StringBuilder();
        if(h >= this.k){
            return result.toString();
        }
        Map<String, Integer> map1 = findMax(nums1, nums1.length, nums2.length, h);
        Map<String, Integer> map2 = findMax(nums2, nums2.length, nums1.length, h);
        if(map1.get("maxValue") == map2.get("maxValue") && map1.get("maxIndex") == -1){
            return result.toString();
        }
        if(map1.get("maxValue") == map2.get("maxValue")){
            String tmp1 = getMaxNumber(Arrays.copyOfRange(nums1, map1.get("maxIndex") + 1, nums1.length), nums2, 1 + h);
            String tmp2 = getMaxNumber(nums1, Arrays.copyOfRange(nums2, map2.get("maxIndex") + 1, nums2.length), 1 + h);
            //get max of tmp1 and tmp2
            for(int i = 0; i < tmp1.length(); i++){
                if(tmp1.charAt(i) > tmp2.charAt(i)){
                    return result.append(map1.get("maxValue")).append(tmp1).toString();
                } else if(tmp1.charAt(i) < tmp2.charAt(i)) {
                    return result.append(map1.get("maxValue")).append(tmp2).toString();
                }
            }
            return result.append(map1.get("maxValue")).append(tmp1).toString();
        } else if (map1.get("maxValue") > map2.get("maxValue")){
            return result.append(map1.get("maxValue")).append(getMaxNumber(Arrays.copyOfRange(nums1, map1.get("maxIndex") + 1, nums1.length), nums2, ++h)).toString();
        } else {
            return result.append(map2.get("maxValue")).append(getMaxNumber(nums1, Arrays.copyOfRange(nums2, map2.get("maxIndex") + 1, nums2.length), ++h)).toString();
        }
    }

    public Map<String, Integer> findMax(int[] nums, int m, int n, int h){
        Map<String, Integer> map = new HashMap<>();
        map.put("maxIndex", -1);
        map.put("maxValue", -1);
        if(m == 0){
            return map;
        }
        int maxIndex = 0;
        int maxValue = nums[maxIndex];
        for(int i = maxIndex + 1; i < Math.min(m, m + n - this.k + h + 1); i++){
            if(nums[i] > maxValue){
                maxIndex = i;
                maxValue = nums[i];
            }
        }
        map.put("maxIndex", maxIndex);
        map.put("maxValue", maxValue);
        return map;
    }
}
