class Solution {
    public int[] maxNumber(int[] nums1, int[] nums2, int k) {
        int [] result = new int[k];
        if(k > nums1.length + nums2.length || k <= 0){
            return null;
        }
        result = getMaxNumber(nums1, nums2, k);
        return result;
    }

    public int[] getMaxNumber(int[] nums1, int[] nums2, int k){
        int [] result = new int[k];
        int m = nums1.length, n = nums2.length;
        Map<String, Integer> map1, map2, tmpMapi1, tmpMapj1, tmpMapi2, tmpMapj2;
        int i = 0, j = 0, tmpi = 0, tmpj = 0;

        for(int h = 0; h < k; h++){
            map1 = findMax(Arrays.copyOfRange(nums1, i, m), m - i, n - j, k - h);
            map2 = findMax(Arrays.copyOfRange(nums2, j, n), n - j, m - i, k - h);
            if(map1.get("maxValue") == map2.get("maxValue")){
                tmpMapi1 = map1;
                tmpMapj1 = map2;
                tmpMapi2 = map1;
                tmpMapj2 = map2;
                tmpi = i;
                tmpj = j;
                //9，10 ； 9，10  怎么处理。
                while(h < k && Math.max(tmpMapi1.get("maxValue"), tmpMapj1.get("maxValue")) == Math.max(tmpMapi2.get("maxValue"), tmpMapj2.get("maxValue"))
                        && tmpMapi1.get("maxValue") >= tmpMapj1.get("maxValue") 
                        && tmpMapj2.get("maxValue") >= tmpMapi2.get("maxValue")){
                    result[h++] = Math.max(tmpMapi1.get("maxValue"), tmpMapj1.get("maxValue"));
                    //always choose nums1
                    tmpi = tmpi + tmpMapi1.get("maxIndex") + 1;
                    tmpMapi1 = findMax(Arrays.copyOfRange(nums1, tmpi, m), m - tmpi, n - j, k - h);
                    tmpMapj1 = findMax(Arrays.copyOfRange(nums2, j, n), n - j, m - tmpi, k - h);
                    //always choose nums2
                    tmpj = tmpj + tmpMapj2.get("maxIndex") + 1;
                    tmpMapi2 = findMax(Arrays.copyOfRange(nums1, i, m), m - i, n - tmpj, k - h);
                    tmpMapj2 = findMax(Arrays.copyOfRange(nums2, tmpj, n), n - tmpj, m - i, k - h);
                    if(tmpMapi1.get("maxIndex") == -1 || tmpMapj2.get("maxIndex") == -1){
                        break;
                    }
                }
                if(Math.max(tmpMapi1.get("maxValue"), tmpMapj1.get("maxValue")) > Math.max(tmpMapi2.get("maxValue"), tmpMapj2.get("maxValue"))){
                    i = tmpi;
                    h--;
                } else {
                    j = tmpj;
                    h--;
                }
            } else if (map1.get("maxValue") > map2.get("maxValue")){
                i = i + map1.get("maxIndex") + 1;
                result[h] = map1.get("maxValue");
            } else {
                j = j + map2.get("maxIndex") + 1;
                result[h] = map2.get("maxValue");
            }
        }
        return result;
    }

    public Map<String, Integer> findMax(int[] nums, int m, int n, int k){
