struct State
{
    string state;
    int act_cost = INT_MAX;
    int est_cost = INT_MAX;

    int cost() const
    {
        return act_cost + est_cost;
    }
};

struct State_Comparator
{
    bool operator()(const State& l, const State& r)
    {
        return l.cost() > r.cost() 
            || l.cost() == r.cost() && l.act_cost >= r.act_cost;
    }
};

class Solution
{
public:
    int openLock(const vector<string>& deadends, const string& target)
    {
        const string start = "0000";
        if (target == start)
        {
            return 0;
        }
        priority_queue<State, vector<State>, State_Comparator> q;
        unordered_set<string> seen(begin(deadends), end(deadends));
        if (seen.count(start))
        {
            return -1;
        }
        int ds[] = {1, -1};

        auto heuristic = [&target](const string& state) {
            int dist = 0;
            for (int i = 0; i < 4; ++i)
            {
                int d1 = abs(target[i] - state[i]);
                int d2 = abs('0' - state[i]) + abs(target[i] - '9') + 1;
                int d3 = abs('0' - target[i]) + abs(state[i] - '9') + 1;
                dist += std::min(std::min(d1, d2), d3);
            }
            return dist;
        };

        q.push(State{
            .state = start,
            .act_cost = 0,
            .est_cost = heuristic(start),
        });
        seen.insert(start);

        while (!q.empty())
        {
            State state = q.top();
            q.pop();

            for (int i = 0; i < 4; ++i)
            {
                for (int d : ds)
                {
                    string nstate = state.state;
                    nstate[i] = (10 + (nstate[i] - '0') + d) % 10 + '0';
                    if (nstate == target)
                    {
                        return state.act_cost + 1;
                    }
                    auto [iter, inserted] = seen.insert(nstate);
                    if (inserted)
                    {
                        q.push(State {
                            .state = nstate,
                            .act_cost = state.act_cost + 1,
                            .est_cost = heuristic(nstate),
                        });
                    }
                }
            }
        }
        return -1;
    }
};
