class Solution {
public:
    int openLock(vector<string>& deadends, string target) {
        int i,j,n,ans=0;

        set<string> s(deadends.begin(),deadends.end());
        if(s.find("0000")!=s.end()) return -1;

        queue<string> q;
        q.push("0000");
        while(!q.empty())
        {
            n=q.size();
            for(i=0;i<n;i++)
            {
                auto temp=q.front();
                q.pop();
                if(temp==target) return ans;
                for(j=0;j<4;j++)
                {
                        string fwd,bwd;
