int openLock(vector<string>& deadends, string target) {
	//list of deadends
	unordered_set<string> st{deadends.begin(),deadends.end()};
	//base cases when begin is a deadend or target is a deadend
	if(st.count("0000") or st.count(target)) return -1;
	//start itself is target
	if(target=="0000") return 0;
	//BFS
	queue<string> Q;
	Q.push("0000");
	int steps=0,s;
	string cur,s1,s2;
	while(!Q.empty()){
		s=Q.size();
		while(s--){
			cur=Q.front();
			Q.pop();
			for(int i=0;i<4;i++){
				//s1 -- > cur with one step clockwise 
				//s2 -- > cur with one step anti clockwise
				s1=s2=cur;
				s1[i]='0'+(cur[i]-'0'+1)%10;
				s2[i]='0'+(cur[i]-'0'+9)%10;
				//if s1 or s2 is target return steps
				if(s1==target or s2==target) return steps+1;
				//else push s1 and s2 into queue and list of deadends 
				//if they are not present in dead ends already
				if(!st.count(s1)){
					Q.push(s1);
					st.insert(s1);
				}
				if(!st.count(s2)){
					Q.push(s2);
					st.insert(s2);
				}
			}
		}
		steps++;
	}
	return -1;
}
