int openLock(vector<string>& deadends, string target) {
        constexpr int NUM = 10;
        unordered_set<string> deads(deadends.begin(), deadends.end());
        unordered_set<string> visited;
        if(deads.count("0000") || deads.count(target)) return -1;
        queue<string> q;
        q.push("0000");
        visited.insert("0000");
        int step = 0;
        while(!q.empty()) {
            int size = q.size();
            for(int i = 0; i < size; i++) {
                string cur = q.front();
                q.pop();
                if(cur == target) return step;
                for(int pos = 0; pos < cur.size(); pos++) {
                    for(int op = -1; op <= 1; op += 2) {
                        int num = ((cur[pos] - '0') + op + NUM) % NUM;
                        string next_cur = cur;
                        next_cur[pos] = num + '0';
                        if(visited.count(next_cur) || deads.count(next_cur)) continue;
                        q.push(next_cur);
                        visited.insert(next_cur);
                    }
                }
            }
            step++;
        }
        return -1;
    }
