            visited[d] = true;
        }
        int t = Integer.parseInt(target);
        Queue<Integer> q = new LinkedList<>();
        q.add(0000);
        while(q.size()>0){
            int l= q.size();
            ans++;
            for(int i=0;i<l;i++){
                int d = q.poll();
                if(visited[d]==true) continue;
                if(d==t) return ans;
                visited[d] = true;
                int d4 = d%10; d=d/10;
                int d3 = d%10; d=d/10;
                int d2 = d%10; d=d/10;
                int d1 = d%10;

