	def openLock(self, deadends: List[str], target: str) -> int:
        # we can put the seen key in dead
        # both works as the same
        dead = set(deadends)
        if "0000" in dead:
            return -1
        q = [("0000", 0)]
        dead.add("0000")
        while len(q) > 0:
            key, dis = q.pop(0)
            if key == target:
                return dis

            neigh = self.getNeigh(key, dead, dis+1)
            if len(neigh) > 0:
                q.extend(neigh)

        return -1


    def getNeigh(self, state, dead, dis):
        output = []
        for i in range(4):
            num = int(state[i])
            for a in ((num-1), (num+1)):
                tmp = state[:i] + str(a%10) + state[i+1:]
                if tmp not in dead:
                    output.append((tmp, dis))
                    dead.add(tmp)

        return output

