class Solution {
    public int openLock(String[] deadends, String target) {
        Set<String> deadendSet = new HashSet<>();
        Set<String> visited = new HashSet<>();
        Queue<String> queue = new LinkedList<>();

        for(String tmp : deadends) deadendSet.add(tmp);

        String start = "0000";
        int step = 0;
        queue.offer(start);
        visited.add("0000");

        while(!queue.isEmpty()){
            int sz = queue.size();
            for(int i = 0; i < sz; i++){
                String tmp = queue.poll();

                if(deadendSet.contains(tmp)) continue;
                if(tmp.equals(target)) return step;

                for(int j = 0; j < 4; j++){
                    String plus = plusOne(tmp, j);
                    String minus = minusOne(tmp, j);

                    if(!visited.contains(plus)){
                        queue.offer(plus);
                        visited.add(plus);
                    }

                    if(!visited.contains(minus)){
                        queue.offer(minus);
                        visited.add(minus);
                    }
                }   
            }
            step++;
        }
        return -1;
    }

    private String plusOne(String str, int index){
        char[] array = str.toCharArray();
        if(array[index] == '9'){
            array[index] = '0';
        }else{
            array[index] += 1;
        }

        return new String(array);
    }

    private String minusOne(String str, int index){
        char[] array = str.toCharArray();
        if(array[index] == '0'){
            array[index] = '9';
        }else{
            array[index] -= 1;
        }

        return new String(array);
    }
}

