        if(target == null || target == "")
            return -1;

        HashSet<string> set = new HashSet<string>(deadends);
        if(set.Contains("0000"))
            return -1;

        HashSet<string> visited =  new HashSet<string>();
        Queue<string> queue = new Queue<string>();
        queue.Enqueue("0000");
        visited.Add("0000");
        int cnt = 0;

        while(queue.Count > 0)
