class Solution {
    public int openLock(String[] deadends, String target) {
        // we need a set to add our deadends to so we can see if our current combo that we would check
        // is a deadend and therefore not try to move in any direction as if we had hit that deadend
        Set<String> deads = new HashSet();
        for (String deadend: deadends) deads.add(deadend);

        // need a seen set so we make sure we're not constantly adding the same combos to our queue
        Set<String> seen = new HashSet();

        // need a queue to store our combos so we can BFS through all possibilities
        Queue<String> combos = new LinkedList<>();
        // we start from 0000 and nulls represent a new level for us
        combos.add("0000");
        combos.add(null);

        // must keep track of potential # of spins (levels || nulls)
        int spins = 0;

        // keep going while you have posibilities        
        while (!combos.isEmpty()) {
            // our possible combination derives from where we could have spun our lock last turn
            String combo = combos.poll();
            // if the combination is null that means we've hit a new level
            if (combo == null) {
                // if there are more attempts in the queue we need to add a new level
                if (combos.peek() != null) {
                    // so we increase the number of spins to reach this point and add a level
                    spins++;
                    combos.add(null);
                }
            } else if (combo.equals(target)) {
                // if we find our target return how many spins it took to get there
                return spins;
            } else if (!deads.contains(combo)) {
                // if our combo is not a deadspin that means we can queue up to our next 8 possible combinations

                // this represents our 4 locks
                for (int i = 0; i < 4; i++) {
                    // this represents the directions we could spin each lock
                    for (int j = -1; j <= 1; j+=2) {
                        // now we get the value of the lock we're changing
                        int lock = combo.charAt(i) - '0';
                        // find out what the value would be after our spin
                        int changedValue = (lock + j + 10) % 10;
                        // create the new combo by replacing the old lock
                        String newCombo = combo.substring(0, i) + ("" + changedValue) + combo.substring(i+1, 4);
                        // if this is a combo we've yet to see before
                        if (!seen.contains(newCombo)) {
                            // then add it to the possible combos
                            // and to the seen set so we don't repeat it in the future.
                            combos.add(newCombo);
                            seen.add(newCombo);
                        }
                    }
                }
            }
        }
        return -1;
    }
}
