        queue<string> q; 

        unordered_map<string,bool> visited;
        int ans=0;
        for(int i=0;i<deadends.size();i++){
            visited[deadends[i]]=true;
        }
        if(!visited["0000"])
            q.push("0000");
        while(!q.empty()){
