    public int OpenLock(string[] dead, string target) {
        const string start = "0000";
        var deadEnds = dead.ToHashSet();
        if (!deadEnds.Add(start) && start != target) return -1;
        var queue = new Queue<string>(new[] {start});

        for (var step = 0; queue.Count > 0; step++)
        for (var thisStepStates = queue.Count; thisStepStates > 0; thisStepStates--) {
            var state = queue.Dequeue();
            if (state == target) return step;
            foreach (var nextState in GetNextStates(state))
                if (deadEnds.Add(nextState)) queue.Enqueue(nextState);
        }
        return -1;
    }

    private static IEnumerable<string> GetNextStates(string state) {
        for (var wheel = 0; wheel < 4; wheel++) {
            yield return MoveUp(state, wheel);
            yield return MoveDown(state, wheel);
        }
    }

    private static string MoveUp(string state, int wheel) {
        var builder = new StringBuilder(state);
        builder[wheel] = Inc(builder[wheel]);
        return builder.ToString();
    }

    private static string MoveDown(string state, int wheel) {
        var builder = new StringBuilder(state);
        builder[wheel] = Dec(builder[wheel]);
        return builder.ToString();
    }

    private static char Inc(char value) => (char) ((value - '0' + 1) % 10 + '0');
    private static char Dec(char value) => (char) ((value - '0' + 9) % 10 + '0');
