class Solution {
    static Set<String> dSet;

    public int openLock(String[] deadends, String target) {
        dSet = new HashSet<>(Arrays.asList(deadends));
        if (dSet.contains("0000")) return -1;
        Queue<String> queue = new LinkedList<>();
        queue.add("0000");
        int res = 0;
        Set<String> visited = new HashSet<>();
        visited.add("0000");
        while (!queue.isEmpty()) {
            int size = queue.size();
            for (int i = 0; i < size; i++) {
                String tmp = queue.poll();
                if (tmp.equals(target)) return res;
                for (String neighbor : getNeighbors(tmp)) {
                    if (visited.contains(neighbor)) continue;
                    queue.add(neighbor);
                    visited.add(neighbor);
                }
            }
            res++;
        }
        return -1;
    }

    public static List<String> getNeighbors(String s) {
        List<String> res = new ArrayList<>();
        char[] arr = s.toCharArray();
        for (int i = 0; i < 4; i++) {
            char c = arr[i];
            //increase
            if (c == '9') arr[i] = '0';
            else arr[i] = (char) (c + 1);
            String tmp = new String(arr);
            if (!dSet.contains(tmp))
                res.add(tmp);
            //decrease
            if (c == '0') arr[i] = '9';
            else arr[i] = (char) (c - 1);
            tmp = new String(arr);
            if (!dSet.contains(tmp))
                res.add(tmp);
            //reverse
            arr[i] = c;
        }
        return res;
    }
}
