                    int inc = pass + (d == 9 ? -9 * diff : diff);
                    if (!visited_s[inc]) {
                        if (visited_e[inc]) return res;
                        visited_s[inc] = true;
                        q_s.push(inc);
                    }
                    int dec = pass + (d == 0 ? 9 * diff : -diff);
                    if (!visited_s[dec]) {
                        if (visited_e[dec]) return res;
                        visited_s[dec] = true;
                        q_s.push(dec);
                    }
                }
            }
        }
        return -1;
    }
};
