            if len(start) > len(end):
                start, end, = end, start
            temp = set()
            for lock in start:
                stop.add(lock)
                for i in range(4):
                    for n in neighbors[lock[i]]:
                        new_lock = lock[:i] + n + lock[i+1:]
                        if new_lock in end:
                            return steps
                        if new_lock in stop:
                            continue     
                        temp.add(new_lock)
            start = temp

