    int openLock(vector<string>& deadends, string target) {

        // storing deadends in a set
        unordered_set<string> st;
        for(auto s:deadends) st.insert(s);

        // if 0000 is in deadend no need to go further
        if(st.find("0000") != st.end()){
            return -1;
        }
        queue<string> q;
        unordered_map<string,int> dist;
        // distance of initial string is set as 0
        dist["0000"] = 0;
        q.push("0000");

        while(!q.empty()){
            string s = q.front();
            q.pop();
            vector<string> moves = next(s);
            for(string t:moves){
                // cant go to this string as its in deadend
                if(st.find(t) != st.end()) continue;
                // we have to go to strings that we have not already visited/seen
                if(dist.find(t) == dist.end()){
                    dist[t] = 1+dist[s];
                    q.push(t);
                }
                if(t == target) return dist[t];
            }
        }
        return -1;
    }
};

